        _1 = <boxed::Box<T> as core::default::Default>::default() -> [return: bb1, unwind unreachable];
    }
    bb1: {
        _0 = boxed::Box::<T>::into_pin(move _1) -> [return: bb2, unwind unreachable];
    }
    bb2: {
        StorageDead(_1);
        return;
    }
}
