pub fn map<U>(this: Self, f: impl FnOnce(T) -> U) -> Box<U> {
        if size_of::<T>() == size_of::<U>() && align_of::<T>() == align_of::<U>() {
            let (value, allocation) = Box::take(this);
            Box::write(
                unsafe { mem::transmute::<Box<MaybeUninit<T>>, Box<MaybeUninit<U>>>(allocation) },
                f(value),
            )
        } else {
            Box::new(f(*this))
        }
    }

