fn new<A: Allocator + Clone>(alloc: A) -> Box<Self, A> {
        let mut leaf = Box::new_uninit_in(alloc);
        unsafe {
            // SAFETY: `leaf` points to a `LeafNode`
            LeafNode::init(leaf.as_mut_ptr());
            // SAFETY: `leaf` was just initialized
            leaf.assume_init()
        }
    }

