pub fn resize<const M: usize>(self, value: bool) -> Mask<T, M>
    where
        LaneCount<M>: SupportedLaneCount,
    {
        // Safety: swizzles are safe for masks
        unsafe {
            Mask::<T, M>::from_int_unchecked(self.to_int().resize::<M>(if value {
                T::TRUE
            } else {
                T::FALSE
            }))
        }
    }
