pub const fn midpoint(self, other: f128) -> f128 {
        const HI: f128 = f128::MAX / 2.;

        let (a, b) = (self, other);
        let abs_a = a.abs();
        let abs_b = b.abs();

        if abs_a <= HI && abs_b <= HI {
            // Overflow is impossible
            (a + b) / 2.
        } else {
            (a / 2.) + (b / 2.)
        }
    }
