pub const fn clamp(mut self, min: f64, max: f64) -> f64 {
        const_assert!(
            min <= max,
            "min > max, or either was NaN",
            "min > max, or either was NaN. min = {min:?}, max = {max:?}",
            min: f64,
            max: f64,
        );

        if self < min {
            self = min;
        }
        if self > max {
            self = max;
        }
        self
    }
