fn spec_fill(&mut self, value: i8) {
        // SAFETY: The pointer is derived from a reference, so it's writable.
        unsafe {
            crate::intrinsics::write_bytes(self.as_mut_ptr(), value.cast_unsigned(), self.len());
        }
    }

