fn <cell::RefCell<T> as clone::Clone>::clone(_1: &cell::RefCell<T>) -> cell::RefCell<T> {
    let mut _0: cell::RefCell<T>;
    let mut _2: T;
    let  _3: &T;
    let mut _4: &cell::Ref<'_, T>;
    let  _5: cell::Ref<'_, T>;
    debug self => _1;
    bb0: {
        StorageLive(_2);
        StorageLive(_4);
        StorageLive(_5);
        _5 = cell::RefCell::<T>::borrow(_1) -> [return: bb1, unwind unreachable];
    }
    bb1: {
        _4 = &_5;
        _3 = <cell::Ref<'_, T> as ops::deref::Deref>::deref(move _4) -> [return: bb2, unwind unreachable];
    }
    bb2: {
        StorageDead(_4);
        _2 = <T as clone::Clone>::clone(_3) -> [return: bb3, unwind unreachable];
    }
    bb3: {
        _0 = cell::RefCell::<T>::new(move _2) -> [return: bb4, unwind unreachable];
    }
    bb4: {
        drop(_5) -> [return: bb5, unwind unreachable];
    }
    bb5: {
        StorageDead(_5);
        StorageDead(_2);
        return;
    }
}
