        _2 = mem::forget::<cell::BorrowRef<'_>>(move _3) -> [return: bb1, unwind unreachable];
    }
    bb1: {
        StorageDead(_3);
        StorageLive(_4);
        _4 = &(_1.0: ptr::non_null::NonNull<T>);
        _0 = ptr::non_null::NonNull::<T>::as_ref::<'_>(move _4) -> [return: bb2, unwind unreachable];
    }
    bb2: {
        StorageDead(_4);
        return;
    }
