pub const fn minmax_by<T, F>(v1: T, v2: T, compare: F) -> [T; 2]
where
    F: [const] FnOnce(&T, &T) -> Ordering,
{
    if compare(&v1, &v2).is_le() { [v1, v2] } else { [v2, v1] }
}

