pub const fn minmax_by_key<T, F, K>(v1: T, v2: T, mut f: F) -> [T; 2]
where
    F: [const] FnMut(&T) -> K + [const] Destruct,
    K: [const] Ord + [const] Destruct,
{
    if f(&v2) < f(&v1) { [v2, v1] } else { [v1, v2] }
}

