pub fn _mm256_unpacklo_epi32(a: __m256i, b: __m256i) -> __m256i {
    unsafe {
        let r: i32x8 = simd_shuffle!(a.as_i32x8(), b.as_i32x8(), [0, 8, 1, 9, 4, 12, 5, 13]);
        transmute(r)
    }
}

