pub fn _mm256_unpacklo_epi64(a: __m256i, b: __m256i) -> __m256i {
    unsafe {
        let r: i64x4 = simd_shuffle!(a.as_i64x4(), b.as_i64x4(), [0, 4, 2, 6]);
        transmute(r)
    }
}

