let a = _mm256_setr_epi64x(0, 1, 2, 3);
let b = _mm256_setr_epi64x(0, -1, -2, -3);

let c = _mm256_unpacklo_epi64(a, b);

let expected = _mm256_setr_epi64x(0, 0, 2, -2);
assert_eq!(_mm256_movemask_epi8(_mm256_cmpeq_epi8(c, expected)), !0);

