pub fn _mm256_insertf32x4<const IMM8: i32>(a: __m256, b: __m128) -> __m256 {
    unsafe {
        static_assert_uimm_bits!(IMM8, 1);
        let b = _mm256_castps128_ps256(b);
        match IMM8 & 0b1 {
            0 => simd_shuffle!(a, b, [8, 9, 10, 11, 4, 5, 6, 7]),
            _ => simd_shuffle!(a, b, [0, 1, 2, 3, 8, 9, 10, 11]),
        }
    }
}

