pub fn _mm256_mask_shuffle_epi32<const MASK: _MM_PERM_ENUM>(
    src: __m256i,
    k: __mmask8,
    a: __m256i,
) -> __m256i {
    unsafe {
        static_assert_uimm_bits!(MASK, 8);
        let r = _mm256_shuffle_epi32::<MASK>(a);
        transmute(simd_select_bitmask(k, r.as_i32x8(), src.as_i32x8()))
    }
}

