            [
                (MASK as u32 & 0b1) * 4 + 0,
                (MASK as u32 & 0b1) * 4 + 1,
                (MASK as u32 & 0b1) * 4 + 2,
                (MASK as u32 & 0b1) * 4 + 3,
                ((MASK as u32 >> 1) & 0b1) * 4 + 0 + 8,
                ((MASK as u32 >> 1) & 0b1) * 4 + 1 + 8,
                ((MASK as u32 >> 1) & 0b1) * 4 + 2 + 8,
                ((MASK as u32 >> 1) & 0b1) * 4 + 3 + 8,
            ],
        );
        transmute(r)
    }
}

