pub fn _mm_alignr_epi64<const IMM8: i32>(a: __m128i, b: __m128i) -> __m128i {
    unsafe {
        static_assert_uimm_bits!(IMM8, 8);
        let imm8: i32 = IMM8 % 2;
        let r: i64x2 = match imm8 {
            0 => simd_shuffle!(a, b, [2, 3]),
            1 => simd_shuffle!(a, b, [3, 0]),
            _ => unreachable_unchecked(),
        };
        transmute(r)
    }
}

