        } else if N <= 16 {
            // Safety: bitmask matches length
            unsafe { self.to_bitmask_impl::<u16, 16>() as u64 }
        } else if N <= 32 {
            // Safety: bitmask matches length
            unsafe { self.to_bitmask_impl::<u32, 32>() as u64 }
        } else {
            // Safety: bitmask matches length
            unsafe { self.to_bitmask_impl::<u64, 64>() }
        }
    }

