pub fn interleave(self, other: Self) -> (Self, Self) {
        let (lo, hi) = self.to_int().interleave(other.to_int());
        // Safety: swizzles are safe for masks
        unsafe { (Self::from_int_unchecked(lo), Self::from_int_unchecked(hi)) }
    }

