pub fn shift_elements_right<const OFFSET: usize>(self, padding: bool) -> Self {
        // Safety: swizzles are safe for masks
        unsafe {
            Self::from_int_unchecked(self.to_int().shift_elements_right::<OFFSET>(if padding {
                T::TRUE
            } else {
                T::FALSE
            }))
        }
    }

