pub const fn clamp(mut self, min: f32, max: f32) -> f32 {
        const_assert!(
            min <= max,
            "min > max, or either was NaN",
            "min > max, or either was NaN. min = {min:?}, max = {max:?}",
            min: f32,
            max: f32,
        );

        if self < min {
            self = min;
        }
        if self > max {
            self = max;
        }
        self
    }

