pub unsafe fn simd_insert_dyn<T, U>(mut x: T, idx: u32, val: U) -> T {
    // SAFETY: `idx` must be in-bounds
    unsafe { (&raw mut x).cast::<U>().add(idx as usize).write(val) }
    x
}

