        StorageLive(_3);
        _3 = _1 as *mut ();
        StorageLive(_4);
        _4 = ptr::metadata::metadata::<U>(_2) -> [return: bb1, unwind unreachable];
    }
    bb1: {
        _0 = ptr::metadata::from_raw_parts_mut::<U, ()>(move _3, move _4) -> [return: bb2, unwind unreachable];
