default unsafe fn load(offset: usize) -> Self {
        let _guard = crate::irq::disable_local();
        let base = crate::arch::cpu::local::get_base() as usize;
        let ptr = (base + offset) as *const Self;
        // SAFETY: Same as `add_assign`.
        unsafe { ptr.read() }
    }

