pub fn append_descriptor(&mut self, descriptor: u128) {
        if self.tail == self.queue_size {
            self.tail = 0;
        }
        self.segment
            .write_val(self.tail * size_of::<u128>(), &descriptor)
            .unwrap();
        self.tail += 1;
    }

