pub(super) fn into_pte(self) -> C::E {
        match self {
            Child::PageTable(node) => {
                let paddr = node.paddr();
                let _ = ManuallyDrop::new(node);
                C::E::new_pt(paddr)
            }
            Child::Frame(paddr, level, prop) => C::E::new_page(paddr, level, prop),
            Child::None => C::E::new_absent(),
        }
    }

