pub fn load(&self, ordering: Ordering) -> IdSet<I> {
        let bits = self
            .bits
            .iter()
            .map(|part| match ordering {
                Ordering::Release => part.fetch_or(0, ordering),
                _ => part.load(ordering),
            })
            .collect();
        IdSet {
            bits,
            phantom: PhantomData,
        }
    }

