        self.bits.iter().enumerate().all(|(idx, part)| {
            if idx == self.bits.len() - 1 && !num_ids.is_multiple_of(BITS_PER_PART) {
                *part == (1 << (num_ids % BITS_PER_PART)) - 1
            } else {
                *part == !0
            }
        })
    }

