unsafe fn bitand_assign(offset: usize, val: Self) {
                // SAFETY: Same as `add_assign`.
                unsafe {
                    core::arch::asm!(
                        concat!("and gs:[{0}], {1", $register_format, "}"),
                        in(reg) offset,
                        in($inout_type) val,
                        options(nostack),
                    );
                }
            }
