unsafe fn load(offset: usize) -> Self {
                let val: Self;
                // SAFETY: Same as `add_assign`.
                unsafe {
                    core::arch::asm!(
                        concat!("mov {0}, gs:[{1}]"),
                        out(reg) val,
                        in(reg) offset,
                        options(nostack, readonly),
                    );
                }
                val
            }
