unsafe fn load(offset: usize) -> Self {
        let val: u8;
        // SAFETY: Same as `add_assign`.
        unsafe {
            core::arch::asm!(
                "mov {0}, gs:[{1}]",
                out(reg_byte) val,
                in(reg) offset,
                options(nostack, readonly),
            );
        }
        debug_assert!(val == 1 || val == 0);
        val == 1
    }
