unsafe fn store(offset: usize, val: Self) {
        let val: u8 = if val { 1 } else { 0 };
        // SAFETY: Same as `add_assign`.
        unsafe {
            core::arch::asm!(
                "mov gs:[{0}], {1}",
                in(reg) offset,
                in(reg_byte) val,
                options(nostack),
            );
        }
    }
