pub(super) fn new(io_mem: &IoMem<Sensitive>) -> Option<Self> {
        if !Self::has_xapic() {
            return None;
        }

        // SAFETY: xAPIC is present.
        assert_eq!(io_mem.paddr(), unsafe { read_xapic_base_address() });
        assert_eq!(io_mem.size(), XAPIC_MMIO_SIZE);
        Some(Self {
            io_mem: io_mem.clone(),
        })
    }
