pub(super) const fn flags_to_cache_policy(flags: PageTableFlags) -> CachePolicy {
    let bits = flags.bits();
    let mut index = 0usize;
    if bits & PageTableFlags::NO_CACHE.bits() != 0 {
        index |= 2;
    }
    if bits & PageTableFlags::WRITE_THROUGH.bits() != 0 {
        index |= 1;
    }
    IA32_PAT_MAPPINGS[index]
}
