pub(crate) fn call_irq_callback_functions(
    trap_frame: &TrapFrame,
    hw_irq_line: &HwIrqLine,
    cpu_priv_at_irq: PrivilegeLevel,
) {
    level::enter(
        move || {
            top_half::process(trap_frame, hw_irq_line);
            bottom_half::process(hw_irq_line.irq_num());
        },
        cpu_priv_at_irq,
    );
}
