pub fn read_more(&mut self, mut reader: impl Read) -> io::Result<usize> {
        let mut buf = BorrowedBuf::from(&mut self.buf[self.filled..]);
        reader.read_buf(buf.unfilled())?;
        self.filled += buf.len();
        Ok(buf.len())
    }

