pub fn bind<P: AsRef<Path>>(path: P) -> io::Result<UnixListener> {
        unsafe {
            let inner = Socket::new(libc::AF_UNIX, libc::SOCK_STREAM)?;
            let (addr, len) = sockaddr_un(path.as_ref())?;
            #[cfg(any(
                target_os = "windows",
                target_os = "redox",
                target_os = "espidf",
                target_os = "horizon"
            ))]
            const backlog: core::ffi::c_int = 128;
            #[cfg(any(
                // Silently capped to `/proc/sys/net/core/somaxconn`.
                target_os = "linux",
                // Silently capped to `kern.ipc.soacceptqueue`.
                target_os = "freebsd",
                // Silently capped to `kern.somaxconn sysctl`.
                target_os = "openbsd",
                // Silently capped to the default 128.
                target_vendor = "apple",
            ))]
            const backlog: core::ffi::c_int = -1;
            #[cfg(not(any(
                target_os = "windows",
                target_os = "redox",
                target_os = "espidf",
                target_os = "horizon",
                target_os = "linux",
                target_os = "freebsd",
                target_os = "openbsd",
                target_vendor = "apple",
            )))]
            const backlog: libc::c_int = libc::SOMAXCONN;

            cvt(libc::bind(inner.as_inner().as_raw_fd(), (&raw const addr) as *const _, len as _))?;
            cvt(libc::listen(inner.as_inner().as_raw_fd(), backlog))?;

            Ok(UnixListener(inner))
        }
    }

