pub fn as_slice(&self) -> &Slice {
        // SAFETY: Slice is just a wrapper for [u8],
        // and self.inner.as_slice() returns &[u8].
        // Therefore, transmuting &[u8] to &Slice is safe.
        unsafe { mem::transmute(self.inner.as_slice()) }
    }

