pub fn send_to_addr(&self, buf: &[u8], socket_addr: &SocketAddr) -> io::Result<usize> {
        unsafe {
            let count = cvt(libc::sendto(
                self.as_raw_fd(),
                buf.as_ptr() as *const _,
                buf.len(),
                MSG_NOSIGNAL,
                (&raw const socket_addr.addr) as *const _,
                socket_addr.len,
            ))?;
            Ok(count as usize)
        }
    }
